(715, 0, 'Elementary Chinese for Advanced Beginners', 'Y', 2011, 7066, 'Y', 'Lecture taught in sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'For students with significant listening and speaking background. Introductory Modern Chinese language course, with emphasis on reading and writing. Covers in one term the equivalent of Chinese Ba and Bb.', '', 'No auditors. May not be taken Pass/Fail. Students must pass a test in listening and speaking to take the course.'),
(716, 106, 'Introduction to Literary Chinese', 'N', 2011, 1185, 'Y', 'Lecture with sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'Basic grammar and the reading of simple historical narrative.', 'At least one year of modern Chinese, or familiarity with Chinese characters through knowledge of Japanese or Korean.', 'An additional lecture slot may be added if enough students enroll, with times to be arranged.'),
(717, 106, 'Introduction to Literary Chinese', 'N', 2011, 3600, 'Y', 'Lecture with sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'Introduction to pre-Qin philosophical texts.', 'Chinese 106a or permission of instructor.', 'An additional lecture slot may be added if enough students enroll, with times to be arranged.'),
(718, 107, 'Intermediate Literary Chinese', 'N', 2011, 3343, 'Y', 'Lecture with sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'A second-year course designed to prepare students for reading and research using materials written in Literary Chinese. The focus in the fall semester will be prose from the Tang and Song dynasties.', 'One year of literary Chinese (Chinese 106 or equivalent).', ''),
(719, 107, 'Intermediate Literary Chinese', 'N', 2011, 6931, 'Y', 'Lecture with sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'A continuation of Chinese 107a, introducing more prose styles as well as poetry and lyric.', 'Chinese 107a or equivalent.', ''),
(720, 120, 'Intermediate Modern Chinese', 'N', 2011, 4283, 'Y', 'Lecture taught in sections', 'For Undergraduates and Graduates', 'Chinese', 0, '', '', 'Modern texts, conversation, reading, and composition.', 'Chinese Bb or equivalent.', 'No auditors. May not be taken Pass/Fail.'),
