(3664, 0, 'Beginning Babylonian', 'N', 2011, 4891, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Akkadian', 0, '', '', 'Introduction to the fundamentals of Babylonian (Akkadian) grammar and the most commonly encountered Neo-Assyrian cuneiform signs.', '', ''),
(3665, 120, 'Intermediate Babylonian', 'N', 2011, 3724, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Akkadian', 0, '', '', '', 'Akkadian grammar, basic vocabulary, knowledge of cuneiform script.', ''),
(3666, 141, 'Akkadian Myths and Epics', 'N', 2011, 7618, 'N', 'Lecture', 'For Undergraduates and Graduates', 'Akkadian', 0, '', '', 'Examination of selected Assyrian and Babylonian myths and epics from the latter second and first millennia B.C.', 'Akkadian grammar, basic vocabulary, knowledge of cuneiform script.', ''),
(3667, 144, 'Akkadian Divination Texts', 'N', 2011, 6734, 'Y', 'Seminar', 'For Undergraduates and Graduates', 'Akkadian', 0, '', '', '', '', ''),
