(4708, 98, 'Tutorial - Junior Year', 'Y', 2011, 32928, 'Y', 'Junior Tutorial', 'Primarily for Undergraduates', 'South Asian Languages, Literatures, and Cultures', 0, '', '', '', '', 'Required of concentrators in the South Asian Languages, Literatures, and Cultures option.'),
(4709, 99, 'Tutorial - Senior Year', 'Y', 2011, 63142, 'Y', 'Senior Tutorial', 'Primarily for Undergraduates', 'South Asian Languages, Literatures, and Cultures', 0, '', '', '', '', 'Required of concentrators in the South Asian Languages, Literatures, and Cultures option.'),
(4710, 100, 'South Asian Language Tutorials', 'Y', 2011, 78249, 'Y', 'Tutorial', 'Primarily for Undergraduates', 'South Asian Languages, Literatures, and Cultures', 0, '', '', 'Individualized study of a South Asian language; emphasis on written expression, reading comprehension and oral fluency. Languages currently offered include Bengali and Thai though others may be approved upon petition to the Director of Undergraduate Studies/Director of Graduate Studies.', '', 'Not open to auditors.'),
(4711, 101, 'Elementary Sanskrit', 'N', 2011, 8140, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Sanskrit', 0, '', '', 'Introduction to Classical Sanskrit, the translocal language of intellectual life in South Asia for much of the last two millennia. This course provides the essential grammar and reading proficiency necessary to take up the language''s many rich literary traditions: scripture (Upanisad), epic (Ramayana and Mahabbarata), poetry, Hindu and Buddhist philosophy, etc. After completing the textbook, we will read a narrative ( Hitopadesa) drawn from one of the most popular literary works in the pre-modern world.', '', ''),
(4712, 101, 'Elementary Sanskrit', 'N', 2011, 6892, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Sanskrit', 0, '', '', 'Continuation of Sanskrit 101a.', '', ''),
(4713, 102, 'Intermediate Sanskrit I', 'N', 2011, 4843, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Sanskrit', 0, '', '', 'A reading course designed to give students the tools necessary for advanced study in Classical Sanskrit. Readings in epic (itihasa) or narrative (katha), poetry (kavya) or systematic thought (sastra) will introduce students to a variety of important genres and their distinctive conventions. A focus upon the Sanskrit tradition''s categories of analysis - grammatical, commentarial and prosodic - will enable students to begin to make sense of original Sanskrit texts as generations of the tradition''s own readers have.', '', ''),
(4714, 102, 'Intermediate Sanskrit II', 'N', 2011, 4916, 'Y', 'Lecture', 'For Undergraduates and Graduates', 'Sanskrit', 0, '', '', 'Continuation of Sanskrit 102a.', '', ''),
(4715, 200, 'Advanced Poetic Sanskrit', 'N', 2011, 3658, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Selected readings from inscriptions and documents.', '', ''),
(4716, 200, 'Advanced Literary Sanskrit', 'N', 2011, 3526, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', '', '', ''),
(4717, 201, 'Advanced Philosophical Sanskrit', 'N', 2011, 9986, 'Y', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Topic to be announced.', '', ''),
(4718, 201, 'Advanced Philosophical Sanskrit', 'N', 2011, 5965, 'Y', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Topic to be announced.', '', ''),
(4719, 202, 'Paninian Grammar', 'N', 2011, 6605, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'This course offers a systematic introduction to the ingenious description of Sanskrit grammar, composed by Panini (c.350 BCE). This enduring tradition forms the basis of indigenous Sanskrit learning until today. Some subsequent texts of his school will also be read.', '', ''),
(4720, 204, 'Introduction to Vedic Sanskrit and Literature', 'N', 2011, 6123, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Introduction to Vedic grammar. Selection of texts from the Atharvaveda.', 'At least one year of Sanskrit.', ''),
(4721, 205, 'Readings from the Rgveda II', 'N', 2011, 338, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Readings and discussions of the oldest Indian text.', 'Reading knowledge of Sanskrit.', ''),
(4722, 206, 'Readings in Sanskrit Narrative Literature: Seminar', 'N', 2011, 25468, 'N', 'Seminar', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'A reading course in genres of Sanskrit narrative literature, suitable for second-year students and above.', '', 'Cross-listed as HDS 3782'),
(4723, 214, 'Ritual Sutras', 'N', 2011, 6626, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Reading and discussion of Sutras and Paddhatis.', '', ''),
(4724, 215, 'Advanced Skt: Medieval Inscriptions', 'N', 2011, 18661, 'Y', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Treatment of a neglected topic of Sanskrit studies. To be read are inscriptions in Sanskrit (and some in Prakrit), concentrating on the hyperbolic poetical style of the introductory verses and the technical language detailing the actual grants or proclamation.', '', ''),
(4725, 216, 'Advanced Skt: Kashmir drama/Prahasana texts', 'N', 2011, 33768, 'N', 'Lecture', 'Primarily for Graduates', 'Sanskrit', 0, '', '', 'Reading of classical Sanskrit texts with a regional, Kashmiri flavor.Concentrates on comedy texts in early drama and verse compositions by Ksemendra.', '', ''),
(4726, 217, 'Introduction to Vedic Studies: language and texts - (New Course)', 'N', 2011, 58445, 'N', 'Lecture', 'Primarily for Undergraduates', 'Sanskrit', 0, '', '', 'Presents a history of Vedic texts and religion. Reading of pertinent texts from the period, 1200-500 BCE.', '', ''),
(4727, 218, 'Advanced Skt: Upanisads - (New Course)', 'N', 2011, 37137, 'N', 'Lecture', 'Primarily for Undergraduates', 'Sanskrit', 0, '', '', 'Reading and interpretation of the earliest philosophical texts against their Vedic background found in the Brahmana texts.', '', ''),
(4728, 219, 'Atharvaveda - (New Course)', 'N', 2011, 97565, 'N', 'Lecture', 'Primarily for Undergraduates', 'Sanskrit', 0, '', '', 'Readings from the second-oldest Indian text, focusing on sorcery stanzas, speculative hymns and those devoted to public and private rituals.', '', ''),
